const form=document.querySelector(".container form"),email=document.querySelector(".container form input[type=email]");function validateEmail(e){return/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(String(e).toLowerCase())}form.addEventListener("submit",e=>{e.preventDefault(),validateEmail(email.value)?(form.classList.remove("error"),email.value=""):form.classList.add("error")});